package com.game.common.cache.redis.bean;

/**
 * 
 */
public class CRobatShowoff {

	private int charm = 0;

	private float gift = 0;

	private float consume = 0;
	
	public int getCharm() {
		return charm;
	}

	public void setCharm(int charm) {
		this.charm = charm;
	}

	public float getGift() {
		return gift;
	}

	public void setGift(float gift) {
		this.gift = gift;
	}

	public float getConsume() {
		return consume;
	}

	public void setConsume(float consume) {
		this.consume = consume;
	}
	
	
}
